import React, { Fragment, useState } from "react";

const InputBlog = () => {
  const [title, setTitle] = useState("insert title");
  const [slug, setSlug] = useState("insert slug");
  const [content, setContent] = useState("insert content");
  const [image, setImage] = useState("upload image");
  const [published, setPublished] = useState(""); // Initialize with null

  const onSubmitForm = async (e) => {
    e.preventDefault();
    const slug = title.toLowerCase().split(" ").join("-"); // Convert title to slug
    try {
      const body = {
        blog_title: title,
        blog_slug: slug,
        blog_content: content,
        blog_image: image,
        published_at: published ? new Date(published).toISOString() : null, // If published is an empty string, set published_at to null
      };
      console.log(body);
      const response = await fetch("http://localhost:5000/blogs", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });
      const responseData = await response.json(); // or response.text() if the response is not JSON
      //console.log(responseData);
      window.location = "/"; // Redirect to the home page after creating a blog
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <h1 className="text-center mt-5">Create Blog</h1>
      <form className="mt-5" onSubmit={onSubmitForm}>
        <input
          type="text"
          className="form-control"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />

        <input
          type="text"
          className="form-control"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="Image URL"
        />
        <input
          type="text"
          className="form-control"
          value={image}
          onChange={(e) => setImage(e.target.value)}
          placeholder="Image URL"
        />

        <button className="btn btn-success">Create Blog</button>
      </form>
    </Fragment>
  );
};

export default InputBlog;
