import React, { Fragment, useEffect, useState } from "react";
import { Link } from "react-router-dom";
// import EditBlog from "./EditBlog";
import DeleteBlog from "./DeleteBlog";

const ListBlogs = () => {
  const [blogs, setBlogs] = useState([]); // Initialize with an empty array
  const [showDeleted, setShowDeleted] = useState(false);

  //delete function
  //   const deleteBlog = async (id) => {
  //     try {
  //       const deleteBlog = await fetch(`http://localhost:5000/blogs/${id}`, {
  //         method: "DELETE",
  //       });
  //       setBlogs(blogs.filter((blog) => blog.blog_id !== id));
  //     } catch (err) {
  //       console.error(err.message);
  //     }
  //   };

  //get all blogs
  const getBlogs = async () => {
    try {
      const response = await fetch("http://localhost:5000/blogs");
      const jsonData = await response.json();
      //console.log(jsonData);
      setBlogs(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getBlogs();
  }, []);

  //filter deleted blogs
  // const filteredBlogs = blogs.filter((blog) =>
  //   showDeleted ? blog.published_at === null || blog.deleted_at !== null : true
  // );
  const filteredBlogs = showDeleted
    ? blogs
    : blogs
        .filter((blog) => blog.published_at !== null)
        .sort((a, b) => new Date(b.published_at) - new Date(a.published_at));

  //get status
  const getBlogStatus = (blog) => {
    if (blog.published_at === null && blog.deleted_at !== null) {
      return "Deleted, never published";
    } else if (blog.published_at === null) {
      return "Unpublished";
    } else if (blog.deleted_at !== null) {
      return "Deleted";
    } else {
      return "Published";
    }
  };

  return (
    <Fragment>
      <label>
        <input
          type="checkbox"
          checked={showDeleted}
          onChange={(e) => setShowDeleted(e.target.checked)}
        />
        Show deleted/unpublished blogs
      </label>
      <table className="table mt-5 text-center">
        <thead>
          <tr>
            <th>Title</th>
            <th>Status</th>

            <th>View</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {/* <tr>
            <td>John</td>
            <td>Doe</td>
            <td>john@example.com</td>
          </tr> */}
          {filteredBlogs.map((blog) => (
            <tr key={blog.blog_id}>
              <td>{blog.blog_title}</td>
              <td>{getBlogStatus(blog)}</td>
              <td>
                <Link
                  to={`/blogs/${blog.blog_slug}`}
                  className="btn btn-primary"
                >
                  View
                </Link>
              </td>
              <td>
                <DeleteBlog blog={blog} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Fragment>
  );
};

export default ListBlogs;
