import React, { Fragment, useState } from "react";

const DeleteBlog = ({ blog }) => {
  //console.log(blog);
  //const blogId = blog.blog_id;
  const [blog_title, setBlogTitle] = useState(blog.blog_title);

  const softDelete = async (e) => {
    e.preventDefault();
    const body = {
      deletedAt: new Date().toISOString(),
    };

    const response = await fetch(
      `http://localhost:5000/blogs/${blog.blog_id}`,
      {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      }
    );
    console.log(response);
    window.location = "/"; // Refresh the page after editing
  };

  return (
    <Fragment>
      <button
        type="button"
        class="btn btn-danger"
        data-toggle="modal"
        data-target={`#id${blog.blog_id}`}
      >
        Delete
      </button>

      <div class="modal" id={`id${blog.blog_id}`}>
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h4 class="modal-title">Delete {blog_title} </h4>
              <button type="button" class="close" data-dismiss="modal">
                &times;
              </button>
            </div>

            <div class="modal-body">
              Are you sure you want to delete {blog_title}
            </div>

            <div class="modal-footer">
              <button
                type="button"
                className="btn btn-danger"
                data-dismiss="modal"
                onClick={(e) => {
                  softDelete(e);
                }}
              >
                Delete
              </button>
              <button
                type="button"
                class="btn btn-warning"
                data-dismiss="modal"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default DeleteBlog;
