import React, { Fragment, useState } from "react";

const DeleteBlog = ({ blog }) => {
  //const blogId = blog.blog_id;
  const [blog_title, setBlogTitle] = useState(blog.blog_title);
  const [deleted_at, setDeletedAt] = useState(blog.deleted_at);

  const softDelete = async (e) => {
    e.preventDefault();
    try {
      const now = new Date();
      const formattedDate =
        now.getFullYear() +
        "-" +
        String(now.getMonth() + 1).padStart(2, "0") +
        "-" +
        String(now.getDate()).padStart(2, "0") +
        " " +
        String(now.getHours()).padStart(2, "0") +
        ":" +
        String(now.getMinutes()).padStart(2, "0") +
        ":" +
        String(now.getSeconds()).padStart(2, "0") +
        "." +
        String(now.getMilliseconds()).padStart(3, "0");

      console.log(formattedDate);

      const body = {
        deleted_at: formattedDate,
      };
      console.log("body", body);
      const response = await fetch(
        `http://localhost:5000/blogs/${blog.blog_id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        }
      );
      console.log(response);
      window.location = "/"; // Refresh the page after editing
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <button
        type="button"
        className="btn btn-danger"
        data-toggle="modal"
        data-target={`#id${blog.blog_id}`}
      >
        Delete
      </button>

      <div className="modal" id={`id${blog.blog_id}`}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">Delete {blog_title} </h4>
              <button type="button" className="close" data-dismiss="modal">
                &times;
              </button>
            </div>

            <div className="modal-body">
              Are you sure you want to delete {blog_title} ?
            </div>

            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-danger"
                data-dismiss="modal"
                onClick={(e) => {
                  softDelete(e);
                }}
              >
                Delete
              </button>
              <button
                type="button"
                className="btn btn-warning"
                data-dismiss="modal"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default DeleteBlog;
