import React, { Fragment, useEffect, useState } from "react";
import EditBlog from "./EditBlog";
import DeleteBlog from "./DeleteBlog";

const ListBlogs = () => {
  const [blogs, setBlogs] = useState([]); // Initialize with an empty array

  //delete function
  //   const deleteBlog = async (id) => {
  //     try {
  //       const deleteBlog = await fetch(`http://localhost:5000/blogs/${id}`, {
  //         method: "DELETE",
  //       });
  //       setBlogs(blogs.filter((blog) => blog.blog_id !== id));
  //     } catch (err) {
  //       console.error(err.message);
  //     }
  //   };

  //get all blogs
  const getBlogs = async () => {
    try {
      const response = await fetch("http://localhost:5000/blogs");
      const jsonData = await response.json();
      //console.log(jsonData);
      setBlogs(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getBlogs();
  }, []);

  return (
    <Fragment>
      <table className="table mt-5 text-center">
        <thead>
          <tr>
            <th>Title</th>
            <th>View</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {/* <tr>
            <td>John</td>
            <td>Doe</td>
            <td>john@example.com</td>
          </tr> */}
          {blogs.map((blog) => (
            <tr key={blog.blog_id}>
              <td>{blog.blog_title}</td>
              <td>
                {/* passing in the blog object as a prop */}
                {/* <EditBlog blog={blog} /> */}
                View
              </td>
              <td>
                <button
                  className=""
                  //   onClick={() => deleteBlog(blog.blog_id)}
                >
                  <DeleteBlog blog={blog} />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Fragment>
  );
};

export default ListBlogs;
