import React, { useState } from "react";
import axios from "axios"; //used to make requests to the server in search

const SearchBar = () => {
  const [input, setInput] = useState("");
  const [results, setResults] = useState([]);

  //search for blogs by title
  const handleInputChange = async (event) => {
    setInput(event.target.value);
    if (event.target.value.length > 0) {
      const response = await axios.get(
        `http://localhost:5000/search?q=${event.target.value}`
      );

      setResults(response.data);
    } else {
      setResults([]);
    }
  };

  return (
    <>
      {/* SEARCH INPUT */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          style={{
            width: "50%",
            height: "40px",
            borderRadius: "20px",
            padding: "10px",
            paddingLeft: "20px",
            fontSize: "16px",
            backgroundColor: "#f0f0f0",
            border: "2px solid rgb(82, 133, 196)",
            outline: "none",
          }}
        />
        <div
          style={{
            width: "40px",
            height: "40px",
            borderRadius: "50%",
            backgroundColor: "#f0f0f0",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            marginLeft: "10px",
          }}
        >
          <img src="/search.svg" alt="Search" style={{ height: "20px" }} />
        </div>
      </div>
      <div>
        {results.map((result) => (
          <div key={result.blog_id}>
            <h2>{result.blog_title}</h2>
          </div>
        ))}
      </div>
    </>
  );
};

export default SearchBar;
